def is_prime(n):
    if n < 2:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

num = 97
result = is_prime(num)
print(f"{num} is {'prime' if result else 'not prime'}")

def dot_product(v1, v2):
    return sum(a * b for a, b in zip(v1, v2))

vec1 = [1, 2, 3]
vec2 = [4, 5, 6]
dot_prod = dot_product(vec1, vec2)
print(f"Dot product: {dot_prod}")

